    private void buscarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_buscarMouseClicked
        Object[] f = new Object[20];
        f = (cons.buscar_mostrarTablaEmpresa(ruc.getText())).clone();                
        for(int i=0; i<17;i++)
        {
            switch(i){
                case 0: ruc.setText(f[i].toString());
                    break;                
                case 1 : razonsocial.setText(f[i].toString());
                    break;
                case 2 : sector.setSelectedItem(f[i].toString());
                    break;
                case 3 : provincia.setSelectedItem(f[i].toString());
                    break;
                case 4 : canton.setSelectedItem(f[i].toString());
                    break;
                case 5: direccion.setText(f[i].toString());
                    break;
                case 6 : telefono.setText(f[i].toString());
                    break;
                case 7 : fax.setText(f[i].toString());
                    break;
                case 8 : email.setText(f[i].toString());
                    break;
                case 9 : pagina.setText(f[i].toString());
                    break;
                case 10 : actividad_secundaria.setText(f[i].toString());
                    break;    
                case 11: actividad_principal.setText(f[i].toString());
                    break;
                case 12 : constitucion.setValue(f[i]);
                    break;
                case 13 : establecimientos.setValue(f[i]);
                    break;
                case 14 : ventas_mayor.setValue(f[i]);
                    break;
                case 15 : ventas_menor.setValue(f[i]);
                    break;                
            }
        }
    }//GEN-LAST:event_buscarMouseClicked

