    private void btnguardar1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnguardar1MouseClicked
        if ((direccion.getText().equals("")) || (ruc.getText().equals("")) || (razonsocial.getText().equals("")) || (sector.getSelectedItem().toString().equals("")) || (provincia.getSelectedItem().toString().equals("")) || (canton.getSelectedItem().toString().equals("")) || (telefono.getText().equals("")) || (fax.getText().equals("")) || (email.getText().equals("")) || (pagina.getText().equals("")) || (actividad_principal.getText().equals("")) || (actividad_secundaria.getText().equals("")) || (constitucion.getValue().toString().equals("")) || (establecimientos.getValue().equals("")) || (ventas_mayor.getValue().toString().equals("")) || (ventas_menor.getValue().toString().equals(""))){
            JOptionPane.showMessageDialog(null, "Todos los campos no contiene datos");
        }
        else 
        {
            String res = (im.Insertar_actualizar_empresa_identificacion(ruc.getText()));
            if (res.equals("Actualizar"))
            {
                Integer pon = (JOptionPane.showConfirmDialog(this, "Desea Actualizar los datos de la empresa "+razonsocial.getText()+"?"));
                if (pon == 0)
                {
                    im.actualizar_empresa(ruc.getText(), razonsocial.getText(), sector.getSelectedItem().toString(), provincia.getSelectedItem().toString(), canton.getSelectedItem().toString(), direccion.getText(), telefono.getText(), fax.getText(), email.getText(), pagina.getText(), actividad_secundaria.getText(), actividad_principal.getText(),Integer.parseInt(constitucion.getValue().toString()), Integer.parseInt( establecimientos.getValue().toString()),Integer.parseInt(ventas_mayor.getValue().toString()),Integer.parseInt(ventas_menor.getValue().toString()));
                    JOptionPane.showMessageDialog(this, "Datos de la Empresa "+razonsocial.getText()+" han sido actualizados");
                }
            }
            else
            {
                im.insertar_empresa(ruc.getText(), razonsocial.getText(), sector.getSelectedItem().toString(), provincia.getSelectedItem().toString(), canton.getSelectedItem().toString(), direccion.getText(), telefono.getText(), fax.getText(), email.getText(), pagina.getText(), actividad_secundaria.getText(), actividad_principal.getText(),Integer.parseInt(constitucion.getValue().toString()), Integer.parseInt( establecimientos.getValue().toString()),Integer.parseInt(ventas_mayor.getValue().toString()),Integer.parseInt(ventas_menor.getValue().toString()));
                JOptionPane.showMessageDialog(this, "Empresa "+razonsocial.getText()+" han sido creada correctamente");
            }
        }
    }//GEN-LAST:event_btnguardar1MouseClicked

